import { Component } from '@angular/core';
import { FormBuilder, FormGroup } from '@angular/forms';
import { Router } from '@angular/router';
import { BookService } from 'src/app/services/book.service';
import { UserService } from 'src/app/services/user.service';
import { NgbRatingModule } from '';
@Component({
  selector: 'app-add-book',
  templateUrl: './add-book.component.html',
  styleUrls: ['./add-book.component.css'],
  imports:[NgbRatingModule]
})
export class AddBookComponent {
  addBookForm:FormGroup
  constructor(private formBuilder:FormBuilder,private bookService:BookService,private userService:UserService,private route:Router){
    this.addBookForm=this.formBuilder.group({
      bookName:"",
      rating:"",
      author:"",
      description:"",
      genre:""
    })
  }
  onAdd(){
    const lentByUserId=this.userService.getUserIdFromToken();
    const bookDetails={...this.addBookForm.value,lentByUserId};
    this.bookService.addBook(bookDetails).subscribe((res)=>{
      console.log(res);
      this.route.navigateByUrl("/dashboard");
    },
    (error)=>{
      console.log(error);
    })
  }
}
