import { Component, OnInit } from '@angular/core';
import { ActivatedRoute, Router } from '@angular/router';
import { BookService } from 'src/app/services/book.service';
import { UserStoreService } from 'src/app/services/user-store.service';
import { UserService } from 'src/app/services/user.service';

@Component({
  selector: 'app-view-details',
  templateUrl: './view-details.component.html',
  styleUrls: ['./view-details.component.css']
})
export class ViewDetailsComponent implements OnInit {
  book:any
  bookId:any
  lentByUserId:any
  lenderName:any
  availableToken:any
  constructor(private bookService:BookService,private router:ActivatedRoute,private userService:UserService,private route:Router,private userStoreService:UserStoreService){
    this.router.params.subscribe((params)=>{
      this.bookId=params['id'];
    })
  }
  ngOnInit(): void {
    this.bookService.getBookById(this.bookId).subscribe((res)=>{
      console.log(res);
      this.book=res;
      this.lentByUserId=res.lentByUserId
      this.userService.getUserById(this.lentByUserId).subscribe((resp)=>{
        this.lenderName=resp.name;
        console.log(resp);
      },(error)=>{
        console.log(error);
      })
    },(error)=>{
      console.log(error);
    })
  }
  borrow(bookId:any){
    const userId=this.userService.getUserIdFromToken();
    this.bookService.borrowBook(bookId,userId).subscribe((res)=>{
      this.route.navigateByUrl("/borrowedBooks");

    },(error)=>{
      console.log(error);
    })
  }
}
<div class="card text-center">
    <div class="card-header">
      {{book.description}} by {{book.author}}
    </div>
    <div class="card-body">
      <h5 class="card-title">{{book.bookName}}</h5>
      <p class="card-text">{{book.genre}}</p>
      <p class="card-text">Lender Name:{{lenderName}}</p>
      <a class="btn btn-primary" (click)="borrow(book.bookId)">Borrow this book</a>
    </div>
    <div class="card-footer text-body-secondary">
      Rating-{{book.rating}}
    </div>
  </div>
