import { Component } from '@angular/core';
import { FormBuilder, FormGroup } from '@angular/forms';
import { Router } from '@angular/router';
import { ToastrService } from 'ngx-toastr';
import { UserService } from 'src/app/services/user.service';

@Component({
  selector: 'app-login',
  templateUrl: './login.component.html',
  styleUrls: ['./login.component.css']
})
export class LoginComponent {
  loginForm:FormGroup
  constructor(private formBuilder:FormBuilder,private route:Router,private userService:UserService,private toastr: ToastrService){
    this.loginForm=this.formBuilder.group({
      username:"",
      password:''
    });
}
onLogin(){
  console.log(this.loginForm.value);
  this.userService.login(this.loginForm.value).subscribe((res)=>{
    console.log(res.token);
    this.userService.storeToken(res.token);
    const name=this.userService.getUsernameFromToken()
    this.toastr.success("Logged in successfully");
    this.route.navigateByUrl("/dashboard");
  },
  (error)=>{
   this.toastr.error("Login failed");
    console.log(error);
  })
}
}
<div class="base">
    <div class="login">
      <form [formGroup]="loginForm" (submit)="onLogin()">
        <div class="mb-3">
          <label for="exampleInputEmail1" class="form-label">Email address</label>

          <input
            type="email"
            class="form-control"
            id="exampleInputEmail1"
            placeholder="Enter your Email"
            aria-describedby="emailHelp"
            formControlName="username"
          />
        </div>

        <div class="mb-3">
          <label for="exampleInputPassword1" class="form-label">Password</label>

          <input
            type="password"
            class="form-control"
            id="exampleInputPassword1"
            placeholder="Enter your Password"
            formControlName="password"
          />
        </div>

        <button type="submit" class="btn btn-primary">Submit</button>




      </form>
    </div>
  </div>
